function NullifyUndefinedValues(obj) {
  // Base case: if obj is not an object or array, return obj
  if (typeof obj !== 'object' || obj === null) {
    return obj;
  }

  // Recursive case: loop through obj and nullify all undefined values
  for (let key in obj) {
    if (obj.hasOwnProperty(key)) {
      if (obj[key] === undefined) {
        obj[key] = null;
      } else if (typeof obj[key] === 'object') {
        obj[key] = NullifyUndefinedValues(obj[key]);
      }
    }
  }

  return obj;
}

// Usage example
const obj = {
  foo: 'bar',
  baz: undefined,
  nestedObj: {
    hello: 'world',
    nestedArray: [1, 2, undefined, 4]
  }
};

const cleanedObj = NullifyUndefinedValues(obj);
console.log(cleanedObj);

